mapper.select:
SELECT
  COUNT(1)
FROM app_info a

	AND a.softwareName LIKE CONCAT('%',#{softwareName},'%')
	AND a.status = #{status}
	AND a.flatformId = #{flatformId}
	AND a.categoryLevel1 = #{categoryLevel1}
	AND a.categoryLevel2 = #{categoryLevel2}
	AND a.categoryLevel3 = #{categoryLevel3}

SELECT a.*, (SELECT valueName FROM data_dictionary WHERE typeCode = "APP_STATUS" AND a.status = data_dictionary.valueId) AS statusName, (SELECT valueName FROM data_dictionary WHERE typeCode = "APP_FLATFORM" AND a.flatformId = data_dictionary.valueId) AS flatformName, (SELECT categoryName FROM app_category WHERE a.categoryLevel1 = app_category.id) AS categoryLevel1Name, (SELECT categoryName FROM app_category WHERE a.categoryLevel2 = app_category.id) AS categoryLevel2Name, (SELECT categoryName FROM app_category WHERE a.categoryLevel3 = app_category.id) AS categoryLevel3Name, (SELECT versionNo FROM app_version WHERE a.versionId=app_version.id) AS versionNo FROM app_info a
AND a.softwareName LIKE CONCAT('%',#{softwareName},'%') AND a.status = #{status} AND a.flatformId = #{flatformId} AND a.categoryLevel1 = #{categoryLevel1} AND a.categoryLevel2 = #{categoryLevel2} AND a.categoryLevel3 = #{categoryLevel3}
LIMIT #{from},#{pageSize}

mapper.update:
UPDATE app_info
softwareName=#{softwareName}, APKName=#{APKName}, supportROM=#{supportROM}, interfaceLanguage=#{interfaceLanguage}, softwareSize=#{softwareSize}, updateDate=#{updateDate}, devId=#{devId}, appInfo=#{appInfo}, `status`=#{status}, onSaleDate=#{onSaleDate}, offSaleDate=#{offSaleDate}, flatformId=#{flatformId}, categoryLevel3=#{categoryLevel3}, downloads=#{downloads}, createdBy=#{createdBy}, creationDate=#{creationDate}, modifyBy=#{modifyBy}, modifyDate=#{modifyDate}, categoryLevel1=#{categoryLevel1}, categoryLevel2=#{categoryLevel2}, logoPicPath=#{logoPicPath}, logoLocPath=#{logoLocPath}, versionId=#{versionId},
where id=#{id}
